package de.robotricker.transportpipes.pipeutils;

import org.bukkit.Material;
import org.bukkit.inventory.ItemStack;

import de.robotricker.transportpipes.TransportPipes;
import de.robotricker.transportpipes.pipes.PipeType;
import de.robotricker.transportpipes.pipes.colored.PipeColor;

public class PipeItemUtils {

	public static final ItemStack ITEM_PIPE_WHITE = InventoryUtils.createSkullItemStack("9f38586a-2ec7-33be-a472-13939b855430", "", "");
	public static final ItemStack ITEM_PIPE_BLUE = InventoryUtils.createSkullItemStack("b3ee4986-c3ad-3199-9536-13cffe591185", "", "");
	public static final ItemStack ITEM_PIPE_RED = InventoryUtils.createSkullItemStack("058a6092-456a-32c9-8e9d-5fcadf620631", "", "");
	public static final ItemStack ITEM_PIPE_YELLOW = InventoryUtils.createSkullItemStack("9bdb3363-b7e5-35ea-9fc6-02b2608ced3e", "", "");
	public static final ItemStack ITEM_PIPE_GREEN = InventoryUtils.createSkullItemStack("7b0b532f-d8cd-3024-bdcc-9afb25c8d790", "", "");
	public static final ItemStack ITEM_PIPE_BLACK = InventoryUtils.createSkullItemStack("7366ff1c-7c05-3c06-a582-71f15641178d", "", "");
	public static final ItemStack ITEM_PIPE_GOLDEN = InventoryUtils.createSkullItemStack("a4ad8e9d-bff3-3549-932d-f92ab895badd", "", "");
	public static final ItemStack ITEM_PIPE_IRON = InventoryUtils.createSkullItemStack("3874f281-eefe-3e26-94ab-d32a53cf3359", "", "");
	public static final ItemStack ITEM_PIPE_ICE = InventoryUtils.createSkullItemStack("f5fb2bb0-b743-3401-9508-141689682ed6", "", "");
	public static final ItemStack ITEM_WRENCH = InventoryUtils.createGlowingItemStack(Material.STICK, (short) 0);

	public static ItemStack getPipeItem(PipeType pipeType, PipeColor pipeColor) {
		switch (pipeType) {
		case COLORED:
			switch (pipeColor) {
			case WHITE:
				return InventoryUtils.changeDisplayName(ITEM_PIPE_WHITE, PipeColor.WHITE.getColorCode() + PipeType.COLORED.getFormattedPipeName());
			case BLUE:
				return InventoryUtils.changeDisplayName(ITEM_PIPE_BLUE, PipeColor.BLUE.getColorCode() + PipeType.COLORED.getFormattedPipeName());
			case RED:
				return InventoryUtils.changeDisplayName(ITEM_PIPE_RED, PipeColor.RED.getColorCode() + PipeType.COLORED.getFormattedPipeName());
			case YELLOW:
				return InventoryUtils.changeDisplayName(ITEM_PIPE_YELLOW, PipeColor.YELLOW.getColorCode() + PipeType.COLORED.getFormattedPipeName());
			case GREEN:
				return InventoryUtils.changeDisplayName(ITEM_PIPE_GREEN, PipeColor.GREEN.getColorCode() + PipeType.COLORED.getFormattedPipeName());
			case BLACK:
				return InventoryUtils.changeDisplayName(ITEM_PIPE_BLACK, PipeColor.BLACK.getColorCode() + PipeType.COLORED.getFormattedPipeName());
			default:
				return null;
			}
		case GOLDEN:
			return InventoryUtils.changeDisplayName(ITEM_PIPE_GOLDEN, PipeType.GOLDEN.getFormattedPipeName());
		case IRON:
			return InventoryUtils.changeDisplayName(ITEM_PIPE_IRON, PipeType.IRON.getFormattedPipeName());
		case ICE:
			return InventoryUtils.changeDisplayName(ITEM_PIPE_ICE, PipeType.ICE.getFormattedPipeName());
		default:
			return null;
		}
	}

	public static ItemStack getWrenchItem() {
		return InventoryUtils.changeDisplayName(ITEM_WRENCH, TransportPipes.instance.getFormattedWrenchName());
	}

	public static boolean isItemStackWrench(ItemStack clickedItem) {
		return InventoryUtils.changeDisplayName(ITEM_WRENCH, TransportPipes.instance.getFormattedWrenchName()).isSimilar(clickedItem);
	}

}
